targets.expr:
max by (owner_name)
(
  max by(job_name )
   (
     kube_job_status_completion_time{namespace="default"} - on (job_name)
       kube_job_created{namespace="default"} 
   ) * on (job_name)
   
    group_left(owner_name)

  (group by (job_name,owner_name) (kube_job_owner{owner_name="$cronjob"}))

)

